import React from 'react';
class PhoneCanvas extends React.Component{

    constructor(props)
    {
        super(props)
        this.state = {
            mask: "",
            case: "",
            widgets: []
        }
        this.canvasCtx = React.createRef();
    }

    render(){
        return (
            <canvas ref={this.refs.canvas} width={this.props.width ?? 700} height={this.props.height ?? 700} className={this.props.className??""}></canvas>
        )
    }

    componentDidMount(){
        this.paint()
    }

    paint(){
        //if(this.canvasCtx){
            this.canvasCtx.current.fillStyle = 'rgb(255, 255, 160)';
            this.canvasCtx.current.fillRect(0, 0, this.refs.canvas.width, this.refs.canvas.width);
        //}
    }

}

export default PhoneCanvas;
