import React from 'react';
class PhoneCanvas extends React.Component{

    constructor(props)
    {
        super(props)
        this.canvasCtx = this.refs.canvas.getContext('2D')
        this.state = {
            mask: "",
            case: "",
            widgets: []
        }
    }

    render(){
        return (
            <canvas ref="canvas" width={this.props.width ?? 700} height={this.props.height ?? 700} className={this.props.className=""}></canvas>
        )
    }

    componentDidMount(){
        this.canvasCtx = this.refs.canvas.getContext('2D')
    }

}

export default PhoneCanvas;
