import React from 'react';
import LoadingPhone from './LoadingPhone';
class PhoneCanvas extends React.Component{

    constructor(props)
    {
        super(props)
        this.state = {
            widgets: [],
            loaded: false
        }
        this.canvas = React.createRef();
    }

    render(){
        return (
            <React.Fragment>
                {this.state.loaded && <canvas ref={this.canvas} width={this.props.width ?? 700} height={this.props.height ?? 700} className={this.props.className??""}></canvas>}
                {!this.state.loaded && <LoadingPhone></LoadingPhone>}
            </React.Fragment>
        )
    }

    componentDidMount(){
        this.canvasContext = this.canvas.current.getContext('2d')
        this.paint()
    }

    paint(){
        if(this.canvasContext){
            this.canvasContext.fillStyle = 'rgb(160, 160, 160)';
            this.canvasContext.fillRect(0, 0, this.canvas.current.width, this.canvas.current.width);
            //this.props.mask ?? this.canvasContext.
        }
    }

}

export default PhoneCanvas;
